<h1 id="循环第二次作业第五关">循环第二次作业第五关</h1>
<pre><code>#include&lt;stdio.h&gt;
int main()
{
    //m=200 n=300
    int m,n,i,k,s=0;
    //a统计整除的个数
    scanf(&quot;%d %d&quot;,&amp;m,&amp;n);
    for(i=m;i&lt;=n;i++)
    {
        int a=0;
        for(k=2;k&lt;i;k++)
        {
            if(i%k==0)
            {
                a++;


            }

        }
        if(a==0)
            {   
                s+=i;
            }
    }
    printf(&quot;%d\n&quot;,s);

    return 0;
}
</code></pre>
